// src/WebSocketClient.js
import React, { useEffect, useState } from 'react';
import socketIOClient from 'socket.io-client';

const WebSocketClient = () => {
    const [sensorData, setSensorData] = useState(null);
    const [connected, setConnected] = useState(false);

    useEffect(() => {
        const socket = socketIOClient('ws://localhost:8080'); // Replace with your WebSocket server URL

        socket.on('connect', () => {
            console.log('Connected to WebSocket server');
            setConnected(true);
        });

        socket.on('disconnect', () => {
            console.log('Disconnected from WebSocket server');
            setConnected(false);
        });

        socket.on('connect_error', (error) => {
            console.error('Connection error:', error);
        });

        socket.on('sensorData', (data) => {
            console.log('Received sensor data:', data);
            setSensorData(data);
        });

        return () => {
            if (connected) {
                socket.disconnect();
            }
        };
    }, [connected]);

    if (!connected) {
        return <div>Connecting to WebSocket server...</div>;
    }

    if (!sensorData) {
        return <div>No sensor data received yet...</div>;
    }

    return (
        <div>
            <h1>WebSocket Client</h1>
            <div>
                <p>Connection Status: {connected ? 'Connected' : 'Disconnected'}</p>
                <p>Sensor ID: {sensorData.sensor_id}</p>
                <p>Coordinates: ({sensorData.coordinates.x}, {sensorData.coordinates.y}, {sensorData.coordinates.z})</p>
                <p>Timestamp: {sensorData.timestamp}</p>
            </div>
        </div>
    );
};

export default WebSocketClient;
