import React, { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

const WebSocketClient = () => {
    const [response, setResponse] = useState("");

    useEffect(() => {
        const socket = socketIOClient("ws://localhost:8080"); // Replace with your server's WebSocket URL

        socket.on("sensorData", data => {
            console.log("Received data from server:", data);
            setResponse(data); // Update state with received data
        });

        return () => {
            socket.disconnect(); // Clean up socket connection on unmount
        };
    }, []);

    return (
        <div>
            <h1>WebSocket Client</h1>
            <p>Received data: {response}</p>
        </div>
    );
};

export default WebSocketClient;
