// src/WebSocketClient.js
import React, { useEffect, useState } from 'react';
import socketIOClient from 'socket.io-client';

const WebSocketClient = () => {
    const [sensorData, setSensorData] = useState({});

    useEffect(() => {
        const socket = socketIOClient('ws://localhost:8080'); // Replace with your WebSocket server URL

        socket.on('sensorData', (data) => {
            console.log('Received sensor data:', data);
            setSensorData(data); // Update state with received data
        });

        return () => {
            socket.disconnect(); // Clean up socket connection on component unmount
        };
    }, []);

    return (
        <div>
            <h1>WebSocket Client</h1>
            <div>
                <p>Sensor ID: {sensorData.sensor_id}</p>
                <p>Coordinates: ({sensorData.coordinates.x}, {sensorData.coordinates.y}, {sensorData.coordinates.z})</p>
                <p>Timestamp: {sensorData.timestamp}</p>
            </div>
        </div>
    );
};

export default WebSocketClient;
